# app/services/default_checklist_service.rb\nclass DefaultChecklistService\n  DEFAULTS_FILE = Rails.root.join('config', 'default_checklist_items.yml')\n\n  def self.load_default_checklist(user)\n    default_items = YAML.safe_load(File.read(DEFAULTS_FILE))\n\n    default_items.each do |identifier, attributes|\n      ChecklistItem.create!(attributes.merge(user: user))\n    end\n  end\nend