require 'RMagick'\n\ndef pdf_to_image(pdf_path, image_path)\n  # Convert PDF to PNG using poppler-utils\n  system("pdftoppm -singlefile #{pdf_path} output_pdf")\n  \n  # Use ImageMagick to create a thumbnail from the generated PNG\n  img = Magick::Image.read('output_pdf-1.png').first\n  img.resize_to_fit!(200, 200) # Resize to fit within 200x200 pixels\n\n  # Save the thumbnail\n  img.write(image_path)\nend\n\n# Usage example\npdf_to_image('example.pdf', 'thumbnail.png')