set :application, "your_application"\nset :deploy_to, "/var/www/app"\nset :repository,  "http://your-svn-repo.com/path/to/branch"\n\nnamespace :deploy do\n  task :default do\n    update_code\n    symlink\n    finalize_update\n  end\n\n  task :update_code, roles: :app do\n    run "svn export #{fetch(:repository)} #{release_path}"\n  end\n\n  task :symlink, roles: :app do\n    run "ln -nfs #{release_path}/config.#{fetch(:stage)}.php #{release_path}/config.php"\n    run "ln -sfnT #{release_path} #{deploy_to}/current"\n  end\nend