require 'stanford-core-nlp'\n\npipeline = StanfordCoreNLP.load(:tokenize, :ssplit)\n\ntext = "Getting data from Mr. Jones felt the warm sun on his face as he stepped out onto the balcony of his summer home in Italy. He was happy to be alive."\ndocument = pipeline.annotate(text)\n\nsentences = document.select{ |sentence| sentence.is_a?(CoreNLP::Sentence) }.map(&:text)\nputs sentences.inspect