# config/initializers/default_region.rb\n\nclass << self\n  attr_reader :default_region\nend\n\ndef self.default_region\n  @default_region ||= find_or_create_default_region\nend\n\nprivate\n\ndef self.find_or_create_default_region\n  region = Region.find_by(uri: "whistler")\n  unless region\n    Rails.logger.warn("Default region 'whistler' not found. Creating a new one.")\n    region = Region.create!(uri: "whistler", name: "Whistler") # Adjust attributes as necessary\n  end\n  region\nend