# config/initializers/maintenance_mode.rb\nMAINTENANCE_MODE_ON = ENV['MAINTENANCE']\n\nclass ApplicationController < ActionController::Base\n  before_action :check_for_maintenance\n\n private\n\n def check_for_maintenance\n   render json: { error: "Service is currently in maintenance mode. Please try again later." }, status: :service_unavailable if MAINTENANCE_MODE_ON == 'true'\n end\nend