module SharedMethods\n  def complex_method(*args)\n    puts "Complex calculations with args: #{args}"\n  end\nend\n\nclass Foo\n  # Include the module for instance methods\n  include SharedMethods\n\n  # Extend the module for class methods\n  extend SharedMethods\nend\n\n# Using as an instance method\nfoo = Foo.new\nfoo.complex_method(1, 2)\n\n# Using as a class method\nFoo.complex_method(3, 4)