class YourModelsController < ApplicationController\n  def create\n    @your_model = YourModel.new(your_model_params)\n\n    if @your_model.save\n      redirect_to @your_model, notice: 'Your model was successfully created.'\n    else\n      render :new, status: :unprocessable_entity\n    end\n  end\n\n  private\n\n  def your_model_params\n    params.require(:your_model).permit(:attribute1, :attribute2, ...)\n  end\nend