require 'pdf/writer'\n\n@pdf = PDF::Writer.new\n\ntext = "x"\nexponent = "-2"\n\n# Set the position where you want to start writing\npos_x, pos_y = 100, @pdf.y\n\n# Write the base number\nbase_number_size = 18\n@pdf.select_font 'Helvetica'\n@pdf.textsize(base_number_size)\n@pdf.add_text_wrap(pos_x, pos_y, 200, "#{text}")\n\n# Calculate position for superscript\nsuperscript_position_x = pos_x + @pdf.get_stringwidth(text, base_number_size) + 1 # add a little space after the base number\nsuperscript_position_y = pos_y + (base_number_size * 0.5) # lift up the exponent\n\n# Write the exponent smaller and higher\nexponent_number_size = base_number_size * 0.6 # make it smaller\n@pdf.textsize(exponent_number_size)\n@pdf.add_text_wrap(superscript_position_x, superscript_position_y, 200, "#{exponent}")\n\n# Close your PDF\n@pdf.save_as("output.pdf")