MERGE INTO oldauth o\nUSING (SELECT AUTHUSERNAME, AUTHUNIQUENAME, DESCRIPTION, MAPPINGAUTHNAME\n       FROM USERS2 \n       WHERE AUTHSOURCEID = 200) n\nON (o.AUTHUSERNAME = n.AUTHUSERNAME)\nWHEN MATCHED THEN\n  UPDATE SET \n    o.AUTHUNIQUENAME = n.AUTHUNIQUENAME,\n    o.DESCRIPTION = n.DESCRIPTION,\n    o.MAPPINGAUTHNAME = n.MAPPINGAUTHNAME\nWHERE o.AUTHSOURCEID = 100;