SELECT TOP 1\n       ml.Message\n   FROM MessageLookup ml\n   WHERE ml.[Key] = @Key\n     AND ml.CampaignTypeID = @CampaignTypeID\n     AND (ml.ServiceProviderID = @ServiceProviderID OR ml.ServiceProviderID IS NULL)\n     AND (ml.DistributorID = @DistributorID OR ml.DistributorID IS NULL)\n     AND (ml.CampaignID = @CampaignID OR ml.CampaignID IS NULL)\n   ORDER BY\n       CampaignID DESC, \n       DistributorID DESC,\n       ServiceProviderID DESC;