-- Find additional tags for topics with tags 'sport' and 'ball'\nSELECT DISTINCT tag.tag_name\nFROM topics t\nJOIN tags_topics tt ON t.id = tt.topic_id\nJOIN tags tag ON tt.tag_id = tag.id\nWHERE t.id IN (\n    SELECT t2.id\n    FROM topics t2\n    JOIN tags_topics tt2 ON t2.id = tt2.topic_id\n    JOIN tags tag2 ON tt2.tag_id = tag2.id\n    WHERE tag2.tag_name IN ('sport', 'ball')\n    GROUP BY t2.id, t2.topic_name\n    HAVING COUNT(DISTINCT tt2.tag_id) >= 2\n)\nAND tag.tag_name NOT IN ('sport', 'ball');