SELECT TO_DATE(TRAIN.DEPARTURE_DATE, 'YYYYMM') AS DEPARTURE_MONTH,\n       COUNT(DISTINCT CASE WHEN PERSON_ID = '28' THEN DEPARTURE_DATE END) AS Mark,\n       COUNT(DISTINCT CASE WHEN PERSON_ID = '29' THEN DEPARTURE_DATE END) AS Chris\nFROM TRAIN\nWHERE DEPARTURE_STATION = 'DUBLIN'\nGROUP BY TO_DATE(TRAIN.DEPARTURE_DATE, 'YYYYMM')\nORDER BY DEPARTURE_MONTH;