-- Schema for an Orders system with normalization\n\nCREATE SCHEMA Sales;\n\n-- Customers Table (1NF, 2NF, 3NF)\nCREATE TABLE Sales.Customers (\n    CustomerID INT PRIMARY KEY AUTO_INCREMENT,\n    FirstName VARCHAR(50) NOT NULL,\n    LastName VARCHAR(50) NOT NULL,\n    Email VARCHAR(100) UNIQUE NOT NULL\n);\n\n-- Orders Table (1NF, 2NF, 3NF)\nCREATE TABLE Sales.Orders (\n    OrderID INT PRIMARY KEY AUTO_INCREMENT,\n    CustomerID INT,\n    OrderDate DATE NOT NULL,\n    TotalAmount DECIMAL(10, 2) CHECK (TotalAmount > 0),\n    FOREIGN KEY (CustomerID) REFERENCES Sales.Customers(CustomerID)\n);\n\n-- Products Table (1NF, 2NF, 3NF)\nCREATE TABLE Sales.Products (\n    ProductID INT PRIMARY KEY AUTO_INCREMENT,\n    Name VARCHAR(50) UNIQUE NOT NULL,\n    Price DECIMAL(10, 2) CHECK (Price > 0),\n    Category VARCHAR(50)\n);\n\n-- OrderDetails Table (BCNF/4NF by breaking out quantity and price in another table)\nCREATE TABLE Sales.OrderDetails (\n    OrderDetailID INT PRIMARY KEY AUTO_INCREMENT,\n    OrderID INT,\n    ProductID INT,\n    Quantity INT DEFAULT 1 CHECK (Quantity > 0),\n    UnitPrice DECIMAL(10, 2) NOT NULL,\n    FOREIGN KEY (OrderID) REFERENCES Sales.Orders(OrderID),\n    FOREIGN KEY (ProductID) REFERENCES Sales.Products(ProductID)\n);