-- Add the Windows Login to SQL Server if it does not exist already\nIF NOT EXISTS (SELECT * FROM sys.server_principals WHERE name = N'DOMAIN\Username')\nBEGIN\n    CREATE LOGIN [DOMAIN\Username] FROM WINDOWS;\nEND\n\n-- Grant necessary server roles and db owner access\nALTER SERVER ROLE sysadmin ADD MEMBER [DOMAIN\Username];\n\nUSE YourDatabaseName; -- Replace with your actual database name\nEXEC sp_addrolemember 'db_owner', 'DOMAIN\Username';