-- SQL Server Example\nBEGIN TRANSACTION;\nBEGIN TRY\n    -- Modify the table schema here\n    ALTER TABLE YourTable ADD NewColumn INT;\n\n    -- Commit if all alterations are successful.\n    COMMIT TRANSACTION;\nEND TRY\nBEGIN CATCH\n    -- Rollback in case of error\n    ROLLBACK TRANSACTION;\n    -- Optionally, rethrow or log the error\n    THROW;\nEND CATCH;\n\n-- Oracle Example\nDECLARE\n  v_error EXCEPTION;\nBEGIN\n  -- Start transaction implicitly with DML or DDL\n  ALTER TABLE YourTable ADD (NewColumn NUMBER);\n\n  -- If no exceptions occur commit changes\n  COMMIT;\nEXCEPTION\n  WHEN OTHERS THEN\n    -- Rollback if there's an error\n    ROLLBACK;\n    -- Optionally, rethrow or log the error\n    RAISE;\nEND;