CREATE PROCEDURE CheckForNewDataAndSendEmail\nAS\nBEGIN\n    DECLARE @LastInsertTime datetime;\n    DECLARE @EmailSent bit;\n\n    -- Get the latest insert time from your table\n    SELECT @LastInsertTime = MAX(CreatedDate)\n    FROM YourTable;\n\n    -- Check if there were any new rows in the last 12 hours\n    IF @LastInsertTime IS NULL OR DATEDIFF(hour, @LastInsertTime, GETDATE()) > 12\n    BEGIN\n        -- Set a flag to indicate that an email needs to be sent\n        SET @EmailSent = 1;\n\n        -- Call your procedure or script here to send the email\n        EXEC SendWarningEmail 'email@example.com', 'No new data in the past 12 hours';\n    END\n    ELSE\n    BEGIN\n        -- No action needed if there is new data\n        SET @EmailSent = 0;\n    END\n\n    -- Optionally, you could log this information for auditing purposes\n    INSERT INTO LogTable (CheckDate, EmailSent)\n    VALUES (GETDATE(), @EmailSent);\nEND;