DECLARE @xml xml = '<Field FieldRowId="1000">\n    <Items>\n        <Item Name="CODE"/>\n        <Item Name="DATE"/>\n    </Items>\n</Field>';\n\n-- Create an internal representation of the XML document.\nDECLARE @idoc int;\nEXEC sp_xml_preparedocument @idoc OUTPUT, @xml;\n\n-- Insert data into the table variable\nINSERT INTO @tmpField ([name], [fieldRowId])\nSELECT \n    T2.Name, \n    T1.FieldRowId\nFROM OPENXML(@idoc, '/Field/Items/Item', 1) WITH (\n    FieldRowId int '../../@FieldRowId' -- Accessing the attribute from the parent node\n) AS T1\nCROSS APPLY (VALUES (T1.Name)) AS T2(Name);\n\n-- Clean up the internal representation.\nEXEC sp_xml_removedocument @idoc;\n\n-- For testing purposes, select all from the temporary table\nSELECT * FROM @tmpField;