CREATE PROCEDURE GetFilteredData\n    @status_id INT = NULL,\n    @date DATE = NULL,\n    @other_parameter VARCHAR(255) = NULL\nAS\nBEGIN\n    DECLARE @query NVARCHAR(MAX);\n\n    SET @query = 'SELECT * FROM table WHERE 1=1';\n\n    IF @status_id IS NOT NULL\n        SET @query += ' AND status_id = @status_id';\n\n    IF @date IS NOT NULL\n        SET @query += ' AND date = @date';\n\n    IF @other_parameter IS NOT NULL\n        SET @query += ' AND other_parameter = @other_param';\n\n    EXEC sp_executesql @query, \n        N'@status_id INT, @date DATE, @other_param VARCHAR(255)', \n        @status_id, @date, @other_parameter;\nEND