DELIMITER //\n\nCREATE PROCEDURE FindByXPath(\n    IN xmlContent TEXT,\n    IN xpathExpression VARCHAR(255)\n)\nBEGIN\n    DECLARE startTag VARCHAR(255);\n    DECLARE endTag VARCHAR(255);\n    DECLARE startPosition INT;\n    DECLARE endPosition INT;\n    DECLARE result TEXT;\n\n    -- Simplified XPath support: extract tag name from the provided xpathExpression\n    SET startTag = SUBSTRING(xpathExpression, LOCATE('/',xpathExpression) + 1);\n    SET startTag = SUBSTRING(startTag, 1, IF(LOCATE('/',startTag) > 0, LOCATE('/',startTag)-1, LENGTH(startTag)));\n    SET endTag = CONCAT('</', startTag, '>');\n\n    -- Find positions\n    SET startPosition = LOCATE(CONCAT('<', startTag), xmlContent);\n    IF startPosition > 0 THEN\n        SET startPosition = startPosition + LENGTH(startTag) + 2; -- Move past the start tag\n        SET endPosition = LOCATE(endTag, xmlContent, startPosition);\n\n        -- Extract result\n        SET result = SUBSTRING(xmlContent, startPosition, endPosition - startPosition);\n    ELSE\n        SET result = NULL;\n    END IF;\n\n    SELECT result AS foundValue;\nEND //\n\nDELIMITER ;