-- View for Candidates\nCREATE VIEW dbo.VIEW_Candidates WITH SCHEMABINDING \nAS \nSELECT 'Candidate' AS Source, CandidateID AS SourceId, LastName + ', ' + FirstName AS SourceName\nFROM dbo.Candidates;\n\nGO\n\nCREATE UNIQUE CLUSTERED INDEX idx_VIEW_Candidates ON dbo.VIEW_Candidates (SourceId);\n\n-- View for Resources\nCREATE VIEW dbo.VIEW_Resources WITH SCHEMABINDING \nAS \nSELECT 'Resource' AS Source, ResourceID AS SourceId, LastName + ', ' + FirstName AS SourceName\nFROM dbo.Resources;\n\nGO\n\nCREATE UNIQUE CLUSTERED INDEX idx_VIEW_Resources ON dbo.VIEW_Resources(SourceId);\n\n-- View for Deals\nCREATE VIEW dbo.VIEW_Deals WITH SCHEMABINDING \nAS \nSELECT 'Deal' AS Source, DealID AS SourceId, CONVERT(varchar(50), Number) + '-' + CONVERT(varchar(50), RevisionNumber) AS SourceName\nFROM dbo.Deals;\n\nGO\n\nCREATE UNIQUE CLUSTERED INDEX idx_VIEW_Deals ON dbo.VIEW_Deals(SourceId);\n\n-- View for Job Orders\nCREATE VIEW dbo.VIEW_JobOrders WITH SCHEMABINDING \nAS \nSELECT 'Job Order' AS Source, JobOrderID AS SourceId, CustomerNumber AS SourceName\nFROM dbo.JobOrders;\n\nGO\n\nCREATE UNIQUE CLUSTERED INDEX idx_VIEW_JobOrders ON dbo.VIEW_JobOrders(SourceId);