DECLARE\n    v_xml XMLTYPE := XMLTYPE('<root><data>...</data></root>'); -- Assume large XML here\n    v_clob CLOB;\n    v_offset NUMBER := 1;\n    v_chunk_size CONSTANT NUMBER := 4000;\n    v_chunk VARCHAR2(4000);\nBEGIN\n    v_clob := v_xml.getClobVal();\n    \n    LOOP\n        EXIT WHEN v_offset > DBMS_LOB.GETLENGTH(v_clob);\n        \n        DBMS_LOB.READ(v_clob, v_chunk_size, v_offset, v_chunk);\n        \n        -- Call remote procedure with v_chunk here\n        \n        v_offset := v_offset + v_chunk_size;\n    END LOOP;\nEND;