-- PostDeploymentScript.sql\n-- Assumes each data script is stored in a 'DataScripts' folder relative to the location of this post-deployment script file\n\n:setvar __ScriptPath "$(bs_BaseDirectory)" \n\n-- Determine the path of this current script file.\nDECLARE @ThisScriptFolder NVARCHAR(4000);\nSET @ThisScriptFolder = ':r $(__ScriptPath)FindScriptFolderPath.sql'\nEXEC sp_executesql N'SET @folder OUTPUT = (SELECT @ThisScriptFolder)', N'@folder NVARCHAR(4000) OUT', @ThisScriptFolder OUTPUT;\n\n:setvar DataScriptsFolder "$(DataScriptsFolder)"\nIF '$(DataScriptsFolder)' = ''\nBEGIN\n    :setvar DataScriptsFolder "$(ProjectPath)\Database\Scripts\DataScripts\"\nEND\n\n-- Include your data scripts here, using the dynamically calculated base path.\n:r $(DataScriptsFolder)Table1_data.sql\n:r $(DataScriptsFolder)Table2_data.sql\n-- Add more table data files as needed