CREATE TRIGGER LogDeletedRows\nON YourTableName -- Replace with your actual table name\nFOR DELETE\nAS\nBEGIN\n    DECLARE @LogText NVARCHAR(MAX)\n    \n    SET @LogText = (\n        SELECT \n            (SELECT \n                t.name AS 'Column',\n                c.text AS 'Value' \n             FROM sys.columns c\n             LEFT JOIN deleted d ON c.column_id = d.column_id\n             WHERE c.object_id = object_id('YourTableName') -- Replace with your actual table name\n             FOR XML PATH('column'), TYPE).value('.', 'nvarchar(max)')\n    )\n\n    DECLARE @LogMessage NVARCHAR(MAX)\n    SET @LogMessage = \n        N'Deleted on: ' + CONVERT(NVARCHAR, GETDATE()) + N'\nRow Data:\n' + @LogText\n\n    INSERT INTO LogTable (LogDetails) -- Make sure this table exists and LogDetails is a TEXT or NVARCHAR(MAX) field\n    VALUES (@LogMessage)\nEND