SET @dbfrom = 'db1';\nSET @dbto = 'db2';\n\n-- Constructing the SELECT query as a string\nSET @select_query = CONCAT('SELECT col2, col1, col3 FROM ', @dbfrom, '.mytable');\n\n-- Constructing the INSERT INTO ... SELECT statement\nSET @insert_query = CONCAT('INSERT INTO ', @dbto, '.mytable (col1, col2, col3) ', @select_query);\n\n-- Preparing and executing the dynamic SQL statement\nPREPARE stmt FROM @insert_query;\nEXECUTE stmt;\nDEALLOCATE PREPARE stmt;