SELECT ogu.nid, ogu.is_active, ogu.uid\nFROM og_uid ogu\nWHERE ogu.nid = 185033 \nAND ogu.is_admin = 0\nAND NOT EXISTS (\n    SELECT 1 FROM bio b WHERE b.uid = ogu.uid\n)\nUNION ALL\nSELECT ogu.nid, ogu.is_active, ogu.uid\nFROM og_uid ogu\nWHERE ogu.nid = 185033 \nAND ogu.is_admin = 0\nAND EXISTS (\n    SELECT 1 FROM bio b WHERE b.uid = ogu.uid\n)\nAND NOT EXISTS (\n    SELECT 1 FROM shirtsize s JOIN bio b ON s.bio_id = b.bio_id WHERE b.uid = ogu.uid\n);