SELECT article_id, title, has_image\nFROM (\n    SELECT article_id, title, has_image,\n           @row_number:=@row_number + 1 AS row_num\n    FROM news_articles, (SELECT @row_number:=0) AS t\n    ORDER BY \n        CASE WHEN @row_number = 0 THEN -has_image ELSE 0 END DESC,\n        published_date DESC\n) AS sorted_articles\nORDER BY CASE WHEN row_num = 1 THEN -sorted_articles.has_image ELSE 0 END DESC, \n         sorted_articles.row_num;