-- Using a Subquery\nSELECT employee_id, department_id, salary\nFROM (\n    SELECT employee_id, department_id, salary,\n           ROW_NUMBER() OVER (PARTITION BY department_id ORDER BY salary DESC) as rn\n    FROM employees\n) ranked_employees\nWHERE rn = 1;\n\n-- Using a CTE\nWITH RankedEmployees AS (\n    SELECT employee_id, department_id, salary,\n           ROW_NUMBER() OVER (PARTITION BY department_id ORDER BY salary DESC) as rn\n    FROM employees\n)\nSELECT employee_id, department_id, salary\nFROM RankedEmployees\nWHERE rn = 1;