UPDATE your_table_name\nSET \n    column1 = CASE WHEN column1 <> 'new_value_for_column1' THEN 'new_value_for_column1' ELSE column1 END,\n    column2 = CASE WHEN column2 <> 'new_value_for_column2' THEN 'new_value_for_column2' ELSE column2 END,\n    last_modified = CASE \n                        WHEN column1 <> 'new_value_for_column1' OR column2 <> 'new_value_for_column2'\n                        THEN CURRENT_TIMESTAMP\n                        ELSE last_modified \n                    END\nWHERE id = your_row_id;