ALTER PROCEDURE [dbo].[sp_Report_InventoryAging]\n    @TitleFlag int = 0,\n    @CompName varchar(100) = 'ALL COMPANIES',\n    @CompBranchId varchar(50) = 'ALL',\n    @StateId varchar(50) = 'All States'\nAS\nBEGIN\n    SELECT *\n    FROM InventoryAgingTable ia\n    WHERE (@TitleFlag = 0 OR (CASE WHEN @TitleFlag = 1 THEN ia.ClearTitle ELSE NOT ia.ClearTitle END))\n      AND (@CompName = 'ALL COMPANIES' OR UPPER(ia.Company) = UPPER(@CompName))\n      AND (@CompBranchId = 'ALL' OR ia.OfficeID = @CompBranchId)\n      AND (@StateId = 'All States' OR ia.StateID = @StateId);\nEND;