DECLARE\n     vLimit  NUMBER := 200000;\n     vOffset NUMBER := 0;\n     vRowCount NUMBER;\n   BEGIN\n     LOOP\n       DELETE FROM table_name WHERE id IN (\n         SELECT id FROM temp_table OFFSET vOffset ROWS FETCH NEXT vLimit ROWS ONLY\n       );\n       \n       vRowCount := SQL%ROWCOUNT;\n       COMMIT;\n       \n       EXIT WHEN vRowCount < vLimit;\n       vOffset := vOffset + vLimit;\n     END LOOP;\n   END;