SELECT \n    conname AS constraint_name,\n    tablename AS table_name,\n    att2.attname AS column_name\nFROM (\n    SELECT \n        c.oid, n.nspname, \n        rel.relname, unnest(c.conkey) AS colnum, \n        c.confrelid as confoid\n    FROM \n        pg_constraint c\n    INNER JOIN \n        pg_class rel ON rel.oid = c.conrelid\n    INNER JOIN \n        pg_namespace n ON n.oid = rel.relnamespace\n) AS subq\nJOIN \n    pg_attribute att2 ON att2.attnum = subq.colnum AND att2.attrelid = subq.confoid\nWHERE \n    subq.nspname = 'your_schema_name' -- Replace with your schema name, usually 'public'\n    AND rel.relname = 'user';