CREATE TABLE Site (\n    Reference INT PRIMARY KEY,\n    Name VARCHAR(255),\n    Charge DECIMAL(10, 2)\n);\n\nCREATE TABLE Residency (\n    SiteReference INT,\n    ResidentReference INT,\n    PRIMARY KEY (SiteReference, ResidentReference),\n    FOREIGN KEY (SiteReference) REFERENCES Site(Reference)\n);\n\nCREATE TABLE Resident (\n    Id SERIAL PRIMARY KEY, -- This is an auto-incrementing ID if needed\n    SiteReference INT,\n    ResidentReference INT,\n    Address VARCHAR(255),\n    Username VARCHAR(255),\n    PasswordHash CHAR(64), -- Use a hash for the password\n    UNIQUE (SiteReference, ResidentReference),\n    FOREIGN KEY (SiteReference) REFERENCES Site(Reference)\n);