-- General Search without hierarchy root\nSELECT DISTINCT CASE \n                    WHEN INSTR(search_string, ' > ') = 0 THEN search_string \n                    ELSE SUBSTR(search_string, 1, INSTR(search_string, ' > ') + 2) || '*' \n                 END AS search_result\nFROM hierarchy_view\nWHERE CONTAINS(search_string, :search_term) > 0;\n\n-- Hierarchical Search with root specified\nSELECT DISTINCT CASE \n                    WHEN INSTR(SEARCH_string, ' > ', LENGTH(:root_level)) = 0 THEN search_string \n                    ELSE SUBSTR(search_string, LENGTH(:root_level) + 3) || '*' \n                 END AS search_result\nFROM hierarchy_view\nWHERE CONTAINS(search_string, :search_term WITHIN (:root_level)) > 0;