-- Creating a table to store IP addresses\nCREATE TABLE IPS (\n    IPAddress VARBINARY(4) NOT NULL\n);\n\n-- Inserting an IPv4 address (e.g., 192.168.1.1)\nINSERT INTO IPS (IPAddress) VALUES (CONVERT(VARBINARY(4), PARSENAME('192.168.1.1', 4) + '.' + PARSENAME('192.168.1.1', 3) + '.' + PARSENAME('192.168.1.1', 2) + '.' + PARSENAME('192.168.1.1', 1)));\n\n-- Selecting and converting the binary IP address back to a string\nSELECT CONVERT(VARCHAR(15), IPAddress) AS IPAddressString FROM IPS;