CREATE TABLE Products (\n    ProductID INT PRIMARY KEY,\n    ProductName VARCHAR(255) NOT NULL\n);\n\nCREATE TABLE Attributes (\n    AttributeID INT PRIMARY KEY,\n    AttributeName VARCHAR(100) NOT NULL\n);\n\nCREATE TABLE Attribute_Values (\n    ValueID INT PRIMARY KEY,\n    AttributeID INT,\n    ValueName VARCHAR(100) NOT NULL,\n    FOREIGN KEY (AttributeID) REFERENCES Attributes(AttributeID)\n);\n\nCREATE TABLE Product_Attributes (\n    ProductID INT,\n    AttributeValueID INT,\n    PRIMARY KEY (ProductID, AttributeValueID),\n    FOREIGN KEY (ProductID) REFERENCES Products(ProductID),\n    FOREIGN KEY (AttributeValueID) REFERENCES Attribute_Values(ValueID)\n);\n\nCREATE TABLE Stock (\n    ProductAttributeCombinationID SERIAL PRIMARY KEY,\n    ProductID INT,\n    StockQuantity INT NOT NULL DEFAULT 0,\n    FOREIGN KEY (ProductID) REFERENCES Products(ProductID)\n);