SELECT t1.ID, t1.Cat, t1.Price, t2.Name AS Name, t2.Abbrv AS Abbrv,\n       1 AS SourceOrder\nFROM Table1 t1 \nLEFT JOIN Table2 t2 ON t1.ID = t2.ID\n\nUNION ALL\n\nSELECT t2.ID, t1.Cat, t1.Price, t2.Name AS Name, t2.Abbrv AS Abbrv,\n       2 AS SourceOrder\nFROM Table2 t2 \nLEFT JOIN Table1 t1 ON t1.ID = t2.ID\nWHERE t1.ID IS NULL -- Ensures we only get IDs from Table2 that are not in Table1\n\n-- Order the combined results\nORDER BY \n    CASE \n        WHEN SourceOrder = 1 THEN t1.Price \n        ELSE 0  -- Assign a default value for non-existent prices\n    END DESC,\n    t2.Abbrv ASC;