-- Load oracle_fdw extension\nCREATE EXTENSION oracle_fdw;\n\n-- Create a server\nCREATE SERVER oradb_server FOREIGN DATA WRAPPER oracle_fdw \nOPTIONS (host 'oracle_host', dbname 'orcl');\n\n-- Create a user mapping\nCREATE USER MAPPING FOR postgres SERVER oradb_server\nOPTIONS (username 'ora_user', password 'ora_pass');\n\n-- Import the foreign table\nIMPORT FOREIGN SCHEMA your_oracle_schema FROM SERVER oradb_server INTO local_schema\nOPTIONS (table_name 'your_oracle_table');