DECLARE @SQL NVARCHAR(MAX) = N'';\n\nSELECT @SQL += \n    'UNION ALL\n     SELECT ''' + QUOTENAME(name, '''') + ''' AS DBCatalog, COUNT(*) AS CNT\n     FROM [' + name + '].[dbo].[LinkedDocumentFolderTable]\n     WHERE IsUnmanagedFolder = 1'\nFROM sys.databases\nWHERE name IN ('TF7-User-Demo-ScheduledRestore-03', 'TF7-TestDatabase-ScheduledRestore');\n\n-- Remove leading UNION ALL\nSET @SQL = STUFF(@SQL, 1, 10, '');\n\nEXEC sp_executesql @SQL;