CREATE TABLE LookupTable (\n    ID INT PRIMARY KEY,\n    OtherTable1ID INT NULL,\n    OtherTable2ID INT NULL,\n    OtherTable3ID INT NULL,\n    CONSTRAINT CHK_SingleColumnOnly CHECK (\n        CASE \n            WHEN OtherTable1ID IS NOT NULL THEN 1 ELSE 0 END +\n            CASE \n            WHEN OtherTable2ID IS NOT NULL THEN 1 ELSE 0 END +\n            CASE \n            WHEN OtherTable3ID IS NOT NULL THEN 1 ELSE 0 END = 1\n    )\n);