SELECT \n    Name,\n    STUFF((\n        SELECT ', ' + OptionColumn\n        FROM (\n            SELECT CASE \n                       WHEN OPTION1 = 1 THEN 'OPTION1'\n                       ELSE NULL\n                   END AS OptionColumn\n            UNION ALL\n            SELECT CASE \n                       WHEN OPTION2 = 1 THEN 'OPTION2'\n                       ELSE NULL\n                   END AS OptionColumn\n            UNION ALL\n            SELECT CASE \n                       WHEN OPTION3 = 1 THEN 'OPTION3'\n                       ELSE NULL\n                   END AS OptionColumn\n            UNION ALL\n            SELECT CASE \n                       WHEN OPTION4 = 1 THEN 'OPTION4'\n                       ELSE NULL\n                   END AS OptionColumn\n            UNION ALL\n            SELECT CASE \n                       WHEN OPTION5 = 1 THEN 'OPTION5'\n                       ELSE NULL\n                   END AS OptionColumn\n        ) AS Options\n        WHERE OptionColumn IS NOT NULL\n        FOR XML PATH(''), TYPE).value('.', 'NVARCHAR(MAX)'), 1, 2, '') AS SelectedOptions\nFROM YourTable