WITH RECURSIVE TimeSeries AS (\n    SELECT MIN(received) AS received FROM YourTable\n    UNION ALL\n    SELECT received + 1 SECOND\n    FROM TimeSeries\n    WHERE received + 1 SECOND <= '2000-01-01 08:00:20'\n)\nSELECT ts.received, COALESCE(t.value, t_prev.value) AS value\nFROM TimeSeries ts\nLEFT JOIN YourTable t ON ts.received = t.received\nLEFT JOIN LAG(t, 1) OVER (ORDER BY ts.received) t_prev\nON ts.received > t.received AND t.value IS NULL\nORDER BY ts.received;