SELECT *\nFROM member\nWHERE MONTH(DOB) = MONTH(CURDATE()) AND DAY(DOB) BETWEEN DAY(CURDATE() - INTERVAL 14 DAY) AND DAY(CURDATE())\n   OR (MONTH(DOB) = MONTH(CURDATE() - INTERVAL 14 DAY) AND DAY(DOB) > DAY(CURDATE() - INTERVAL 14 DAY))\n   OR (DAYOFYEAR(DOB) >= DAYOFYEAR(CURDATE()) - 14 \n        AND DAYOFYEAR(DOB) <= DAYOFYEAR(CURDATE()))