SELECT \n    OrganizationName,\n    SUM(CASE WHEN ReceivableStatus = 'Current' THEN InvoiceFee ELSE 0 END) AS Current,\n    SUM(CASE WHEN ReceivableStatus = '30-60 days' THEN InvoiceFee ELSE 0 END) AS [30-60 days],\n    SUM(CASE WHEN ReceivableStatus = '60-90 days' THEN InvoiceFee ELSE 0 END) AS [60-90 days],\n    SUM(CASE WHEN ReceivableStatus = 'over 90 days' THEN InvoiceFee ELSE 0 END) AS [over 90 days],\n    SUM(InvoiceFee) AS Total\nFROM v_InvoicesFreelanceOutstanding\nGROUP BY OrganizationName, OrganizationID\nORDER BY OrganizationID;