CREATE FUNCTION RemoveSpanishChars(inputStr VARCHAR(255))\nRETURNS VARCHAR(255)\nBEGIN\n    DECLARE outputStr VARCHAR(255);\n    SET outputStr = inputStr;\n\n    -- Replace Spanish characters with ASCII compatible characters\n    SET outputStr = REPLACE(outputStr, 'á', 'a');\n    SET outputStr = REPLACE(outputStr, 'é', 'e');\n    SET outputStr = REPLACE(outputStr, 'í', 'i');\n    SET outputStr = REPLACE(outputStr, 'ó', 'o');\n    SET outputStr = REPLACE(outputStr, 'ú', 'u');\n    SET outputStr = REPLACE(outputStr, 'Á', 'A');\n    SET outputStr = REPLACE(outputStr, 'É', 'E');\n    SET outputStr = REPLACE(outputStr, 'Í', 'I');\n    SET outputStr = REPLACE(outputStr, 'Ó', 'O');\n    SET outputStr = REPLACE(outputStr, 'Ú', 'U');\n    SET outputStr = REPLACE(outputStr, 'ñ', 'n');\n    SET outputStr = REPLACE(outputStr, 'Ñ', 'N');\n\n    RETURN outputStr;\nEND;\n\n-- Usage\nSELECT RemoveSpanishChars(nombre) FROM tu_tabla;