-- Create a Sequence\n   CREATE SEQUENCE MyCount_seq START WITH 1 INCREMENT BY 1;\n\n   -- Create the Table without the identity column\n   CREATE TABLE MyCount (\n       Line NUMBER PRIMARY KEY\n   );\n\n   -- Create a Trigger for automatic incrementation\n   CREATE OR REPLACE TRIGGER MyCount_trigger \n   BEFORE INSERT ON MyCount \n   FOR EACH ROW\n   BEGIN\n       SELECT MyCount_seq.NEXTVAL INTO :NEW.Line FROM dual;\n   END;