-- Table to store individual event instances\nCREATE TABLE EventInstances (\n    InstanceID INT PRIMARY KEY AUTO_INCREMENT,\n    EventID INT NOT NULL,\n    StartTime DATETIME NOT NULL,\n    EndTime DATETIME NOT NULL,\n    FOREIGN KEY (EventID) REFERENCES Events(EventID)\n);\n\n-- Table for storing recurring patterns (weekly, monthly, etc.)\nCREATE TABLE RecurringPatterns (\n    PatternID INT PRIMARY KEY AUTO_INCREMENT,\n    Frequency VARCHAR(255) NOT NULL, -- e.g., 'WEEKLY', 'MONTHLY'\n    Specifics VARCHAR(255) NOT NULL  -- e.g., 'Monday and Wednesday', 'Second Thursday', 'Third Friday'\n);\n\n-- Table to represent the master events\nCREATE TABLE Events (\n    EventID INT PRIMARY KEY AUTO_INCREMENT,\n    Name VARCHAR(255) NOT NULL,       -- Name of the event, such as Mass or Confession\n    PatternID INT                     -- Optional reference if the event has a recurring pattern\n    FOREIGN KEY (PatternID) REFERENCES RecurringPatterns(PatternID)\n);\n\n-- Table to store one-time exceptions to recurring events\nCREATE TABLE EventExceptions (\n    ExceptionID INT PRIMARY KEY AUTO_INCREMENT,\n    EventID INT NOT NULL,              -- The base event\n    InstanceOverride DATE NOT NULL,      -- Specific date this override applies to\n    StartTime DATETIME,                -- Optional: specific start time if different from usual\n    EndTime DATETIME,                  -- Optional: specific end time if different from usual\n    FOREIGN KEY (EventID) REFERENCES Events(EventID)\n);