-- Sample Table structure for demonstration\nCREATE TABLE Employees (\n    ID INT PRIMARY KEY,\n    Name NVARCHAR(100),\n    Position NVARCHAR(50)\n);\n\n-- Insert sample data into the Employees table\nINSERT INTO Employees (ID, Name, Position) VALUES\n(1, 'John Doe', 'Developer'),\n(2, 'Jane Smith', 'Manager');\n\n-- SQL script to generate XML\nDECLARE @xmlData XML;\n\nSELECT *\nFROM Employees\nFOR XML PATH('Employee'), ROOT('Employees'), ELEMENTS XSINIL;\n\n-- If you want to output this directly to a file using T-SQL, it's not straightforward as\n-- SQL Server 2005 does not support direct file write operations from T-SQL.\n-- However, you can capture the XML result in a variable and then use a separate process or script (like PowerShell) to save the file.\n\nSET @xmlData = (\n    SELECT *\n    FROM Employees\n    FOR XML PATH('Employee'), ROOT('Employees'), ELEMENTS XSINIL\n);\n\nSELECT @xmlData;