delete t\n    from masterTable t\n    join incomingDataTable inc on\n        (\n            CASE \n                WHEN RTRIM(LTRIM(inc.TextField)) = '' THEN ''\n                ELSE RTRIM(LTRIM(inc.TextField))\n            END\n        =\n            CASE \n                WHEN RTRIM(LTRIM(t.TextField)) = '' THEN ''\n                ELSE RTRIM(LTRIM(t.TextField))\n            END\n        )\n    where RTRIM(LTRIM(t.TextField)) <> '';