CREATE TRIGGER SetDefaultTimeBasedOnCategory\nON YourTableName\nFOR INSERT\nAS\nUPDATE YourTableName\nSET TimeTaken = CASE \n                    WHEN i.WorkCategory = 'A' AND i.TimeTaken IS NULL THEN 1.0 -- 1 hour corresponds to 1.0 in decimal or integer format\n                    WHEN i.WorkCategory = 'B' AND i.TimeTaken IS NULL THEN 2.0 -- 2 hours corresponds to 2.0 in decimal or integer format\n                    ELSE i.TimeTaken  -- In case of a different category, leave as is or handle accordingly.\n                END\nFROM YourTableName t\nINNER JOIN inserted i ON t.YourIDColumn = i.YourIDColumn;